import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { findAllByTitle } from '@testing-library/react';

// const { ReactComponent } = require("*.svg");



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      List: [],
    Main_List: [],
    number: 0
    }
  }
  componentDidMount() {
    fetch('http://newsapi.org/v2/everything?q=apple&from=2020-07-08&to=2020-07-08&sortBy=popularity&apiKey=88d4f86e316140e089929ed953098371')
      .then((response) => {
        return response.json();
        // console.log(response, "ROHIT");
      })
      .then((data) => {
        // this.setState({
        //   articles: data.articles
        // })
       var first_10 = (data.articles.slice(0,10))
       console.log(first_10);

      })
  }

  render() {
    console.log(this.state);

    return (
      <div className="App">
       <div className="header">
          <h1>GOOGLE NEWS..</h1>
        </div><br/>
      {this.state.articles.map((item, index) => {
        return(
            <div>
              <div className="card">
                <img src={item.urlToImage} style={{width:"60%"}}/>
                <div>
                  <h1 className="title">{item.title}</h1>
                  <br/>
                  <p className="author">{item.author}</p><br/>
                  <p>{item.description}</p><br/>
                  <p><button><a href={item.url} target="_blank"><i class="fa fa-dribbble">Read More</i></a></button></p>
                  </div>
              </div>
              <br/>
            </div>


        )})
      }
      <p><button><a href="#" target="_blank"><i class="fa fa-dribbble">NEXT PAGE</i></a></button></p><br></br>
      <p><button><a href="#" target="_blank"><i class="fa fa-dribbble">PREVIOUS PAGE</i></a></button></p>


      </div>
     )}
  // code tester 


}

export default App;
