import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { findAllByTitle } from '@testing-library/react';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      articles:[]
    }
  }
  componentDidMount(){
    fetch('http://newsapi.org/v2/everything?q=apple&from=2020-07-08&to=2020-07-08&sortBy=popularity&apiKey=88d4f86e316140e089929ed953098371')
    .then((response)=>{
      return response.json(); 
    })
    .then((data)=>{
      this.setState({
        articles: data.articles
      })
    })
  }
  render(){
    // console.log(this.state);
    return (
      <div className="App">
        {this.state.articles.map((item, index)=>{
          return (
            <div className="Main-div">
              <h2 style={{backgroundColor:'blue'}}>GOOGLE NEWS API</h2>
              <h1 style= {{textAlign:'center'}}>
                {item.title}
              </h1>
              <b>
                {item.author}
              </b>
              <h3>  
                {item.description}
              </h3>
              <img src={item.urlToImage} style = {{width:'50vw'}}/>
              <p style = {{textAlign:'center'}}>
                {item.content}
              </p> 
              <a href ={item.url}>Read More</a>
            </div>
          )
        })}

      </div>
    )
  }
}

export default App;
