import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { findAllByTitle } from '@testing-library/react';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      articles:[]
    }
  }
  componentDidMount(){
    fetch('http://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=88d4f86e316140e089929ed953098371')
    .then((response)=>{
      return response.json(); 
    })
    .then((data)=>{
      this.setState({
        articles: data.articles
      })
    })
  }
  render(){
    console.log(this.state);
    return (
      <div className="App">
        {this.state.articles.map((item, index)=>{
          return (
            <h2>
              {item.title}
            </h2>
          )
        })}

      </div>
    )
  }
}

export default App;
