import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { findAllByTitle } from '@testing-library/react';

// const { ReactComponent } = require("*.svg");



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    }
  }
  componentDidMount() {
    fetch('http://newsapi.org/v2/everything?q=apple&from=2020-07-08&to=2020-07-08&sortBy=popularity&apiKey=88d4f86e316140e089929ed953098371')
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        this.setState({
          articles: data.articles
        })
      })                   
  }

  render() {
    console.log(this.state);

    return (
      <div className="App">
       {this.state.articles.map((item, index)=>{
        //  console.log("rohit");

         return (


           <div className="title">
              <h2>{item.title}</h2>
              <div>
         <h3>{item.author}</h3>
              </div>
              <div className="description">
         <h4>{item.description}</h4>
              </div>

              <div className="image">
                <img src={item.urlToImage} style={{width:'50vw'}}/>

              </div>
              <div className="content">
                  <p>{item.content}</p>
              </div>
              <div className="more-content">
                <a href={item.url}>READ MORE</a>
              </div>

           </div>

         )
       })}

      </div>
     ) 

  }

}

export default App;
