        // })
       var first_10 = (data.articles.slice(0,10))
       console.log(first_10);
       this.setState({ List: data.articles, Main_List: first_10, number: 10 });

      })
  }

  render() {
    console.log(this.state);

    return (
      <div className="App">
       <div className="header">
          <h1>GOOGLE NEWS..</h1>
        </div><br/>
      {this.state.articles.map((item, index) => {
        return(
            <div>
              <div className="card">
                <img src={item.urlToImage} style={{width:"60%"}}/>
                <div>
                  <h1 className="title">{item.title}</h1>
                  <br/>
                  <p className="author">{item.author}</p><br/>
                  <p>{item.description}</p><br/>
                  <p><button><a href={item.url} target="_blank"><i class="fa fa-dribbble">Read More</i></a></button></p>
                  </div>
              </div>
              <br/>
            </div>


